import React, { useState, Fragment } from "react";
import { Route, Routes } from "react-router-dom";
import "./App.css";
import Header from "./components/Layout/Header/Header";
import CurrencyConverter from "./components/Currency/Converter/Converter";
import ConversionHistory from "./components/Currency/ConversionHistory/ConversionHistory";

import Container from "@mui/material/Container";



const CurrencyConversionContext = React.createContext({});


const [conversionHistory, setConversionHistory] = useState([]);

function App() {
  return (
    <Fragment>

      <CurrencyConversionContext.Provider value={{ conversionHistory, setConversionHistory }}>
        <Header />
        <Container maxWidth="lg">
          <Routes>
            <Route path="/" element={<CurrencyConverter />} />
            <Route path="/currency-converter" element={<CurrencyConverter />} />
            <Route path="conversion-history" element={<ConversionHistory />} />
          </Routes>
        </Container>
      </CurrencyConversionContext.Provider>
    </Fragment>
  );
}

export default App;
