import React, {
  Fragment,
  useEffect,
  useRef,
  useState,
  useCallback,
} from "react";
import Button from "@mui/material/Button";

import ExchangeHistroy from "../ExchangeHistory/ExchangeHistory";
import ExchangeView from "./ExchangeView";
import {
  getCurrency,
  convertCurrency,
  getExchangeHistory,
} from "../../../helper/Api";
import "./Converter.css";

const CurrencyConverter = () => {
  const selectedAmount = useRef();

  const [currencyList, setCurrencyList] = useState([]);
  let [currencyTo, setCurrencyTo] = useState("INR");
  let [currencyFrom, setCurrencyFrom] = useState("USD");

  const [currencyRateTo, setCurrencyRateTo] = useState(0);
  const [currencyRateFrom, setCurrencyRateFrom] = useState(0);

  const [exchangeRate, setExchangeRate] = useState(null);
  const [exchangeRateHistory, setExchangeRateHistory] = useState([]);

  const [min, setMin] = useState(0);
  const [max, setMax] = useState(0);
  const [average, setAverage] = useState(0);

  const [chartData, setChartData] = useState([]);
  const [chartLabels, setChartLabels] = useState([]);

  const handleSwapCurrency = (event) => {
    setCurrencyFrom(currencyTo);
    setCurrencyTo(currencyFrom);
    [currencyFrom, currencyTo] = [currencyTo, currencyFrom];
  };

  const currencyToHandler = (event) => {
    event.preventDefault();
    const to = event.target.value;
    setCurrencyTo(to);
  };

  const currencyFromHandler = (event) => {
    event.preventDefault();
    const from = event.target.value;
    setCurrencyFrom(from);
  };

  const convertHandler = (event) => {
    event.preventDefault();
    const payload = {
      amount: selectedAmount.current.value,
      from: currencyFrom,
      to: currencyTo,
    };
    const api = convertCurrency(payload);
    api.then((response) => {
      setExchangeRate(response);
      setCurrencyRateTo(currencyList[currencyTo]);
      setCurrencyRateFrom(currencyList[currencyFrom]);
      exchangeHistoryHandler(7);
    });
  };

  const getDates = (selectedDuration) => {
    const currentDate = new Date();
    const date = currentDate.getDate();
    const month = currentDate.getMonth() + 1;
    const year = currentDate.getFullYear();

    currentDate.setDate(date - selectedDuration);
    const dateAgo = currentDate.getDate();
    const monthAgo = currentDate.getMonth() + 1;
    const yearAgo = currentDate.getFullYear();

    return {
      start_date: `${yearAgo}-${monthAgo}-${dateAgo}`,
      end_date: `${year}-${month}-${date}`,
    };
  };

  const exchangeHistoryHandler = (selectedDuration) => {
    const payload = getDates(selectedDuration);
    const api = getExchangeHistory(payload);
    api.then((response) => {
      const rates = response.rates;
      const allCurrecnyVal = Object.entries(rates).map((item) =>
        parseFloat(item[1][currencyTo])
      );

      const allDates = Object.entries(rates).map((item) => item[0]);

      setChartData(allDates);
      setChartLabels(allCurrecnyVal);

      const minVal = Math.min(...allCurrecnyVal);
      setMin(minVal);

      const maxVal = Math.max(...allCurrecnyVal);
      setMax(maxVal);

      const avgVal =
        allCurrecnyVal.reduce((a, b) => a + b, 0) / allCurrecnyVal.length;
      setAverage(avgVal);

      setExchangeRateHistory(rates);
    });
  };

  const getData = useCallback(() => {
    const api = getCurrency();
    api.then((response) => {
      setCurrencyList(response.rates);
    });
  }, []);

  useEffect(() => {
    getData();
  }, [getData]);

  return (
    <Fragment>
      <form>
        <h1>I want to convert</h1>
        <div className="form-group">
          <div className="form-control">
            <label htmlFor="amount">Amount</label>
            <input
              type="text"
              name="amount"
              ref={selectedAmount}
              defaultValue="1"
            />
          </div>
          <div className="form-control">
            <label htmlFor="From">From</label>
            <select
              name="from"
              value={currencyFrom}
              onChange={currencyFromHandler}
            >
              {Object.entries(currencyList).map((currentValue) => {
                return (
                  <option key={currentValue[1]} value={currentValue[0]}>
                    {currentValue[0]}
                  </option>
                );
              })}
            </select>
          </div>
          <div className="form-control f-0">
            <button
              type="button"
              className="btn-invert"
              onClick={handleSwapCurrency}
            >
              <span className="material-icons">compare_arrows</span>
            </button>
          </div>
          <div className="form-control">
            <label htmlFor="From">To</label>
            <select value={currencyTo} name="from" onChange={currencyToHandler}>
              {Object.entries(currencyList).map((currentValue) => {
                return (
                  <option key={currentValue[1]} value={currentValue[0]}>
                    {currentValue[0]}
                  </option>
                );
              })}
            </select>
          </div>
          <div className="form-control">
            <Button className="btn btn-primary" onClick={convertHandler}>
              Convert
            </Button>
          </div>
        </div>
      </form>
      {exchangeRate && (
        <ExchangeView
          exchangeRate={exchangeRate}
          ratesTo={currencyRateTo}
          ratesFrom={currencyRateFrom}
        />
      )}
      {chartData.length > 0 && (
        <ExchangeHistroy
          exchangeHistoryHandler={exchangeHistoryHandler}
          exchangeRateHistory={exchangeRateHistory}
          currencyTo={currencyTo}
          currencyFrom={currencyFrom}
          min={min}
          max={max}
          average={average}
          chartLabel={chartLabels}
          chartData={chartData}
        />
      )}
    </Fragment>
  );
};

export default CurrencyConverter;
