import { Fragment, useState } from "react";

import "./ExchangeHistory.css";
import Charts from "../../UI/Chart";

const ExchangeHistroy = (props) => {
  const currency = props.currencyTo;
  const exchangeHistory = props.exchangeRateHistory;
  const minCurrencyValue = props.min;
  const maxCurrencyValue = props.max;
  const averageCurrencyValue = props.average;
  const [showReport, setShowReport] = useState(false);

  const getExchangeHistory = (event) => {
    props.exchangeHistoryHandler(event.target.value);
  };

  const switchReportHandler = (event) => {
    console.log(event.target.value);
  };

  return (
    <Fragment>
      <h3>Exchange History</h3>
      <form>
        <div className="form-group">
          <div className="form-control">
            <label htmlFor="From">Duration</label>
            <select name="date" onChange={getExchangeHistory}>
              <option value="7">7 days</option>
              <option value="14">14 days</option>
              <option value="30">30 days</option>
            </select>
          </div>
          <div className="form-control radio">
            <input
              type="radio"
              id="html"
              name="view_type"
              defaultValue="table"
              onClick={switchReportHandler}
            />
            <label htmlFor="html">Table</label>
          </div>
          <div className="form-control radio">
            <input
              type="radio"
              id="css"
              name="view_type"
              defaultValue="chart"
              onClick={switchReportHandler}
            />
            <label htmlFor="css">Chart</label>
          </div>
        </div>
        <div className="view-conversion">
          <div className="exchange-rate-datewise">
            <table>
              <thead>
                <tr>
                  <th align="left">Date</th>

                  <th align="left">Exchange Rate</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(exchangeHistory).map((currentValue) => {
                  return (
                    <tr key={currentValue[0]}>
                      <td>{currentValue[0]}</td>
                      <td>{currentValue[1][currency]}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          <div className="statistics">
            <table>
              <thead>
                <tr>
                  <th align="left">Statistics</th>
                  <th align="left"></th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Lowest</td>
                  <td>{minCurrencyValue}</td>
                </tr>
                <tr>
                  <td>Highest</td>
                  <td>{maxCurrencyValue}</td>
                </tr>
                <tr>
                  <td>Average</td>
                  <td>{averageCurrencyValue}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
        {}
        <Charts data={props.chartData} label={props.chartLabel} />
      </form>
    </Fragment>
  );
};

export default ExchangeHistroy;
